sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    gs.info('loading badge configuration');
    try {
        if (current.isNewRecord()) {
			current.parent_request_num = parent.sys_id;
        } 

        if (current.user && current.user.photo) {
            gs.info('setting User image from Master User info i.e., sys_user');
            current.cardholderimage = current.user.photo;
            var userAttachments = new GlideSysAttachment();
            var sysUserImage = userAttachments.getAttachments('ZZ_YYsys_user', current.user.sys_id);
            while (sysUserImage.next())
                if (sysUserImage.getValue('file_name') === 'photo') {
                    current.attachment_id = sysUserImage.getValue('sys_id');
                    current.image_content_type = sysUserImage.getValue('content_type');
                    gs.info('attachment_id ::' + current.attachment_id + ' :: current.image_content_type   :: ' + current.image_content_type);
                    //var base64Content = userAttachments.getContentBase64(sysUserImage);
                    //gs.info('base64Content loaded');
                    //current.userimage = "data:image/png;base64, "+base64Content;
                }
        } else if (current.cardholderimage) {
            gs.info('getting User image associated in badge request page');
            var attachment = new GlideSysAttachment();
            var agr = attachment.getAttachments('ZZ_YYx_545192_badgeoffi_userbadges', current.sys_id);
            while (agr.next())
                if (agr.getValue('file_name') === 'cardholderimage') {
                    current.attachment_id = agr.getValue('sys_id');
                    current.image_content_type = agr.getValue('content_type');
                    gs.info('attachment_id ::' + current.attachment_id + ' :: current.image_content_type   :: ' + current.image_content_type);

                    //if we want base64 based on image_content_type
                    //var base64Local = attachment.getContentBase64(agr);
                    //current.userimage = "data:image/png;base64, "+base64Local;
                }
        }
        var r = new sn_ws.RESTMessageV2('x_545192_badgeoffi.Get Badge Configuration', 'Default GET');
        var response = r.executeAsync();
        response.waitForResponse(30);
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();
        gs.info('response' + httpStatus);
        var responseObj = JSON.parse(responseBody);
        if (null != responseObj && null != responseObj.result) {
            gs.info('setting badge configuration to fields');
            current.logo = responseObj.result[0].logo;
            current.template = '<html><body>'+responseObj.result[0].svg +'</body></html>';
            current.companyname = responseObj.result[0].companyname;
            current.userimage = responseObj.result[0].instanceattachmenturl + current.attachment_id + ".iix";
        }
    } catch (ex) {
        var message = ex.message;
        gs.error('message' + message);
    }
})(current, previous);
